import { useState } from "react"

const AddArticle = ()=>{
    const [formData, setFormdata] = useState(
        {
            'title':'',
            'description':''
        }
    )

    const handleChange = (event)=>{
        setFormdata(
            {   
                ...formData,
                [event.target.name]:event.target.value
            }
        )
    }
    return( 
        <form className="form">
            <div className="form-group">
                <input type="text" name="tittle"placeholder="Naujienos pavadinimas" className="m-1 form-control" onChange={handleChange}>
                </input>
            </div>
            <div className="form-group">
                <textarea name="description" placeholder="Naujienos tekstas" className="m-1 form-control" ></textarea>
            </div>
            <div className="form-group">
                <button className="btn btn-primary">Saugoti</button>
            </div>

        </form>
    )
}

export default AddArticle
